package tamilnadu.chennai;

public class Loopings {

	public static void main(String[] args) {
		Loopings lp = new Loopings();
		lp.loop1();
	}

	private void loop1() {
		// TODO Auto-generated method stub
		int flowers = 64;
		int temple = 0;
		while(flowers>0);
		{
			flowers = flowers/2;
			temple = temple+1;
			
		}
		System.out.println(temple);
	}
	
	
}
